trigger.script:
if IsQuestComplete("r6m1_HelpShip_Quest") then trigger:Deactivate()
GetEntityByName("ToR6M2"):SetProperty("Active",1)
CreateNewDummyObject("ferry_work","PLOT",-1,-1,CVector(834.120,215,3344.586),Quaternion(0.0000,-0.8351,0.0000,0.5501),0) end

if QuestStatus("r5m2_KillWantedBandit_Quest")==Q_CANBEGIVEN then trigger:Deactivate()
TakeQuest("r5m2_KillWantedBandit_Quest")
TActivate("cinIten") end

trigger:Deactivate()
SetTolerance(1105, 1112, RS_NEUTRAL)
local listofpos = {CVector(getPos("SpawnBandit1_loc")),CVector(getPos("SpawnBandit2_loc")),CVector(getPos("SpawnBandit3_loc"))}
local btteam = CreateTeam("BanditsTeam", 1105, listofpos, {"r1m1_sml04","Scout02","Scout01"}, CVector(getPos("BattleWithBandits_loc")), nil, Quaternion(0.036, -0.661, 0.038, 0.748))
if btteam then SetVar("CountBanditsDie",0)
TActivate("trBanditsVehDie") else LOG("TRIGGER ERROR: Can't create BanditsTeam") end
