trigger.script:
local time = GetVar("Time").AsNumList
for i=1,7 do local mover = getObj("mover"..i) if mover==nil then local moverId = CreateNewObject { prototypeName = "cinematicMover", objName = "mover"..i } local mover = GetEntityByID( moverId )
local controlledObj = GetEntityByName("movecube"..i) if mover and controlledObj then mover:SetObjAndPath( controlledObj:GetId(), "Cube"..i.."Move", time[i-1] ) end end end
